namespace GetIt

open System

/// Defines a pen used to draw lines.
type Pen =
    {
        /// True, if the pen is turned on, otherwise false.
        IsOn: bool
        /// The thickness of the pen.
        Weight: float
        /// The color of the pen.
        Color: RGBAColor
    }

module internal Pen =
    let ``default`` = { IsOn = false; Weight = 1.; Color = RGBAColors.black }

type internal PlayerId = PlayerId of Guid

module internal PlayerId =
    let create () = PlayerId (Guid.NewGuid())

type SpeechBubble =
    | Say of string
    | AskString of string
    | AskBool of string

type OneOfMany<'a> = private {
    CurrentIndex: int
    Items: 'a array
}
module internal OneOfMany =
    let create items currentIndex =
        if currentIndex < 0 || currentIndex > Array.length items then
            failwithf "`currentIndex` (%d) is not a valid index of the array (%A)" currentIndex items
        { CurrentIndex = currentIndex; Items = items }
    let current v = Array.item v.CurrentIndex v.Items
    let next v =
        { v with CurrentIndex = (v.CurrentIndex + 1) % v.Items.Length }

    let map fn v =
        { CurrentIndex = v.CurrentIndex; Items = Array.map fn v.Items }


/// Defines a player.
/// The player doesn't necessarily have been added to the scene.
type PlayerData =
    {
        /// The factor that is used to change the size of the player.
        SizeFactor: float
        /// The position of the player's center point.
        Position: Position
        /// The rotation of the player.
        Direction: Degrees
        /// The pen that belongs to the player.
        Pen: Pen
        /// The current speech bubble that belongs to the player.
        SpeechBubble: SpeechBubble option
        /// The costumes of the player.
        Costumes: OneOfMany<SvgImage>
        /// The layer on which the player is drawn.
        Layer: int
        /// True, if the player should be drawn, otherwise false.
        IsVisible: bool
    }
    /// The current costume of the player.
    member this.Costume with get() = OneOfMany.current this.Costumes

    /// The actual size of the player.
    member this.Size with get() = this.Costume.Size * this.SizeFactor

    /// The rectangular bounds of the player.
    /// Note that this doesn't take into account the current rotation of the player.
    member this.Bounds
        with get() =
            {
                Position =
                    {
                        X = this.Position.X - this.Size.Width / 2.
                        Y = this.Position.Y - this.Size.Height / 2.
                    }
                Size = this.Size
            }

    /// <summary>
    /// Copies the current player data and changes only the size factor.
    /// </summary>
    /// <param name="sizeFactor">The size factor to use for the player data copy.</param>
    member this.WithSizeFactor sizeFactor = { this with SizeFactor = sizeFactor }

    /// <summary>
    /// Copies the current player data and changes only the position.
    /// </summary>
    /// <param name="position">The position to use for the player data copy.</param>
    member this.WithPosition position = { this with Position = position }

    /// <summary>
    /// Copies the current player data and changes only the position.
    /// </summary>
    /// <param name="x">The X-position to use for the player data copy.</param>
    /// <param name="y">The Y-position to use for the player data copy.</param>
    member this.WithPosition (x, y) = { this with Position = { X = x; Y = y } }

    /// <summary>
    /// Copies the current player data and changes only the direction.
    /// </summary>
    /// <param name="direction">The direction to use for the player data copy.</param>
    member this.WithDirection direction = { this with Direction = direction }

    /// <summary>
    /// Copies the current player data and changes only the pen.
    /// </summary>
    /// <param name="pen">The pen to use for the player data copy.</param>
    member this.WithPen pen = { this with Pen = pen }

    /// Copies the current player data and turns on the pen.
    member this.WithPenOn () = { this with Pen = { this.Pen with IsOn = true } }

    /// Copies the current player data and turns off the pen.
    member this.WithPenOff () = { this with Pen = { this.Pen with IsOn = false } }

    /// Copies the current player data and sets the visibility.
    member this.WithVisibility (isVisible: bool) = { this with IsVisible = isVisible }

    /// <summary>
    /// Copies the current player data and changes only the pen weight.
    /// </summary>
    /// <param name="penWeight">The pen weight to use for the player data copy.</param>
    member this.WithPenWeight penWeight = { this with Pen = { this.Pen with Weight = penWeight } }

    /// <summary>
    /// Copies the current player data and changes only the pen color.
    /// </summary>
    /// <param name="penColor">The pen color to use for the player data copy.</param>
    member this.WithPenColor penColor = { this with Pen = { this.Pen with Color = penColor } }

    /// <summary>
    /// Creates a player with a list of costumes.
    /// The player is placed at (0,0), has 0 degrees rotation and the default pen.
    /// </summary>
    /// <param name="costumes">The costumes of the player.</param>
    static member Create costumes =
        {
            SizeFactor = 1.
            Position = Position.zero
            Direction = Degrees.zero
            Pen = Pen.``default``
            SpeechBubble = None
            Costumes = OneOfMany.create (Seq.toArray costumes) 0
            Layer = 0
            IsVisible = true
        }

    /// <summary>
    /// Creates a player with a single costume.
    /// The player is placed at (0,0), has 0 degrees rotation and the default pen.
    /// </summary>
    /// <param name="costume">The costume of the player.</param>
    static member Create costume =
        PlayerData.Create [ costume ]

